use Google\Cloud\Spanner\SpannerClient;

$spanner = new SpannerClient();

$database = $spanner->connect('my-instance', 'my-database');

$database->runTransaction(function (Transaction $t) {
    // do stuff.

    $t->commit();
});

$modifiedRowCount = $transaction->executeUpdate('UPDATE Posts SET content = @content WHERE id = @id', [
    'parameters' => [
        'content' => 'Hello world!',
        'id' => 10
    ]
]);

$statement = "UPDATE Posts SET title = 'Updated Title' " .
    "WHERE STRUCT<Title STRING, Content STRING>(Title, Content) = @post";

$postValue = new StructValue();
$postValue->add('Title', 'Updated Title')
          ->add('Content', 'Sample Content');

$postType = new StructType();
$postType->add('Title', Database::TYPE_STRING)
         ->add('Content', Database::TYPE_STRING);

$modifiedRowCount = $transaction->executeUpdate($statement, [
    'parameters' => [
        'post' => $postValue
    ],
    'types' => [
        'post' => $postType
    ]
]);

use Google\Cloud\Spanner\Database;

$res = $transaction->executeUpdateBatch([
    [
        'sql' => 'UPDATE posts SET post_status = @status WHERE author_id = @authorId',
        'parameters' => [
            'status' => 'unpublished',
            'authorId' => 1
        ]
    ], [
        'sql' => 'UPDATE authors SET author_permissions = @permissions WHERE author_id = @authorId',
        'parameters' => [
            'permissions' => null,
            'authorId' => 1
        ],
        'types' => [
            'permissions' => Database::TYPE_ARRAY
        ]
    ]
]);

if ($res->error()) {
    echo 'An error occurred: ' . $res->error()['status']['message'];
} else {
    echo 'Updated ' . array_sum($res->rowCounts()) . ' row(s) ' .
         'across ' . count($res->rowCounts()) . ' statement(s)';
}

if ($transaction->isRetry()) {
    echo 'This is a retry transaction!';
}

use Google\Cloud\Spanner\Database;
use Google\Cloud\Spanner\Timestamp;

$values = [
    new Timestamp(new \DateTimeImmutable),
    null
];

$result = $transaction->execute('SELECT @timestamp as timestamp', [
    'parameters' => [
        'timestamp' => array_rand($values)
    ],
    'types' => [
        'timestamp' => Database::TYPE_TIMESTAMP
    ]
]);

$timestamp = $result->rows()->current()['timestamp'];

use Google\Cloud\Spanner\ArrayType;
use Google\Cloud\Spanner\Database;

$result = $transaction->execute('SELECT @emptyArrayOfIntegers as numbers', [
    'parameters' => [
        'emptyArrayOfIntegers' => []
    ],
    'types' => [
        'emptyArrayOfIntegers' => new ArrayType(Database::TYPE_INT64)
    ]
]);

$row = $result->rows()->current();
$emptyArray = $row['numbers'];

use Google\Cloud\Spanner\Database;
use Google\Cloud\Spanner\StructType;

$result = $transaction->execute('SELECT @userStruct.firstName, @userStruct.lastName', [
    'parameters' => [
        'userStruct' => [
            'firstName' => 'John',
            'lastName' => 'Testuser'
        ]
    ],
    'types' => [
        'userStruct' => (new StructType())
            ->add('firstName', Database::TYPE_STRING)
            ->add('lastName', Database::TYPE_STRING)
    ]
]);

$row = $result->rows()->current();
$fullName = $row['firstName'] . ' ' . $row['lastName']; // `John Testuser`

use Google\Cloud\Spanner\Database;
use Google\Cloud\Spanner\Result;
use Google\Cloud\Spanner\StructValue;
use Google\Cloud\Spanner\StructType;

$res = $transaction->execute('SELECT * FROM UNNEST(ARRAY(SELECT @structParam))', [
    'parameters' => [
        'structParam' => (new StructValue)
            ->add('foo', 'bar')
            ->add('foo', 2)
            ->addUnnamed('this field is unnamed')
    ],
    'types' => [
        'structParam' => (new StructType)
            ->add('foo', Database::TYPE_STRING)
            ->add('foo', Database::TYPE_INT64)
            ->addUnnamed(Database::TYPE_STRING)
    ]
])->rows(Result::RETURN_NAME_VALUE_PAIR)->current();

echo $res[0]['name'] . ': ' . $res[0]['value'] . PHP_EOL; // "foo: bar"
echo $res[1]['name'] . ': ' . $res[1]['value'] . PHP_EOL; // "foo: 2"
echo $res[2]['name'] . ': ' . $res[2]['value'] . PHP_EOL; // "2: this field is unnamed"
